mapper.insert:
		INSERT 
	  INTO PIXIV_DATA
	    (UMA_CODE
	    ,UPLOAD_DATE
	    ,PIXIV_AUTHOR
	    ,PIC_ID
	    )
    SELECT
	    #{uma_code}
	    ,#{upload_date} 
	    ,#{author}
	    ,#{pic_id}
    FROM DUAL

    WHERE NOT EXISTS
    (
    SELECT
    UMA_CODE,
    PIC_ID
    FROM
    PIXIV_DATA
    WHERE
    UMA_CODE = #{uma_code}
    AND
    PIC_ID = #{pic_id}

    )
