mapper.insert:
        INSERT 
          INTO TBL_MEMBER A
        (
          A.MEMBER_NO, A.MEMBER_ID, A.MEMBER_PWD
        , A.MEMBER_NAME, A.EMAIL, A.BIRTHDAY
        , A.GENDER, A.PHONE, A.ADDRESS
        ) 
		VALUES
		(
		  SEQ_MEMBER_NO.NEXTVAL, #{ id }, #{ pwd }
		, #{ name }, #{ email }, #{ birthDay }
		, #{ gender }, #{ phone }, #{ address }
		)
